-- Convert string to character set
local function createCharacterSet(text)
    local characterTable = {}
    for position, codePoint in utf8.codes(text) do
        characterTable[utf8.char(codePoint)] = true
    end
    return characterTable
end

-- Check if all characters in string are in the set
local function isStringInCharacterSet(text, characterSet)
    for position, codePoint in utf8.codes(text) do
        local character = utf8.char(codePoint)
        if not characterSet[character] then
            return false
        end
    end
    return true
end

-- CJK character blocks definition (non-symbol areas)
local cjkCharacterBlocks = {
    { 0x4E00,  0x9FFF },  -- CJK Unified Ideographs
    { 0x3400,  0x4DBF },  -- CJK Unified Ideographs Extension A
    { 0x20000, 0x323AF }, -- CJK Unified Ideographs Extension B-H
    { 0x2EBF0, 0x2EE5F }, -- CJK Unified Ideographs Extension I
    { 0x2E80,  0x2EFF },  -- CJK Radicals Supplement
    { 0x2F00,  0x2FDF },  -- Kangxi Radicals
    { 0x31C0,  0x31EF },  -- CJK Strokes
    { 0x3300,  0x33FF },  -- CJK Compatibility
    { 0xF900,  0xFAFF },  -- CJK Compatibility Ideographs
    { 0xFE30,  0xFE4F },  -- CJK Compatibility Forms
    { 0x2F800, 0x2FA1F }, -- CJK Compatibility Ideographs Supplement
    { 0x3190,  0x319F },  -- Kanbun
    { 0x2FF0,  0x2FFF },  -- Ideographic Description Characters
    { 0x1F200, 0x1F2FF }, -- Enclosed Ideographic Supplement
    { 0x1F000, 0x1F02F }, -- Mahjong Tiles
    { 0x2600,  0x26FF },  -- Miscellaneous Symbols (Taiji, YinYang, etc.)
    { 0x4DC0,  0x4DFF },  -- Yijing Hexagram Symbols
    { 0x1D300, 0x1D35F }, -- Tai Xuan Jing Symbols
    { 0x17000, 0x187FF }, -- Tangut
    { 0x18800, 0x18AFF }, -- Tangut Components
    { 0x18D00, 0x18D7F }, -- Tangut Supplement
    { 0x18B00, 0x18CFF }, -- Khitan Small Script
    { 0xE000,  0xF8FF },  -- Private Use Area (Yuhao roots in this area)
    { 0x1B000, 0x1B0FF }, -- Kana Supplement
    { 0x1B100, 0x1B12F }, -- Kana Extended-A
}

-- Check if character is within Unicode blocks
local function isCharacterInUnicodeBlocks(characterCode, unicodeBlocks)
    for index, blockRange in ipairs(unicodeBlocks) do
        if (characterCode >= blockRange[1]) and (characterCode <= blockRange[2]) then
            return true
        end
    end
    return false
end

-- Check if all characters in string are within Unicode blocks
local function isStringInUnicodeBlocks(text, unicodeBlocks)
    for position, characterCode in utf8.codes(text) do
        if not isCharacterInUnicodeBlocks(characterCode, unicodeBlocks) then
            return false
        end
    end
    return true
end

-- Check if string is in character set or contains non-CJK characters
local function isStringInCharacterSetOrNonCJK(text, characterSet)
    local inCharacterSet = isStringInCharacterSet(text, characterSet)
    local inCJKBlocks = isStringInUnicodeBlocks(text, cjkCharacterBlocks)
    return inCharacterSet or not inCJKBlocks
end

-- Main filter function
local function charset_filter(input, env)
    -- Preload common character set
    local commonCharacterTable = createCharacterSet(commonCharacterSet)
    
    -- Bypass filter if input starts with '`'
    if env.engine.context.input:match("^[`]") then
        for candidate in input:iter() do
            yield(candidate)
        end
        return
    end
    
    -- Check filter switch state
    local isFilterEnabled = env.engine.context:get_option("charset_filter")
    
    for candidate in input:iter() do
        local isEligible = isStringInCharacterSetOrNonCJK(candidate.text, commonCharacterTable)
        -- Show candidate if: (1) In common set (2) Non-CJK (3) Filter disabled
        if isEligible or not isFilterEnabled then
            yield(candidate)
        end
    end
end

return charset_filter